REPLACE INTO `Directions` SET `ID`='1047', `RecipeID`='1047', `SortOrder`='0', `Directions`='In a small mixing bowl, combine balsamic vinegar, sugar, water, mustard, and salt and pepper to taste. Mix well. If the consistency is too watery, add more mustard until the desired consistency is reached. Cover, and chill before serving';
