REPLACE INTO `Directions` SET `ID`='3370', `RecipeID`='3370', `SortOrder`='0', `Directions`='You already know how to make these 1 Take each celery stick and put 1 Tbs of peanut butter on each one. 2 Stick four rasins on each celery stick. 3 Dig in.';
