('5916', '5', 'Greek Orzo Pasta Salad'),
('5916', '6', 'Dinner in a Pumpkin'),
('5916', '7', 'Clean Eating Basic White Beans Recipe'),
('5916', '8', 'Rich and Delicious Vegetarian Chili'),
('5916', '9', 'Tweet'),
